import { API_URL } from '../config';

const createActionNames = (action) => {
  return `app/status/${action}`;
};

const createActionName22 = actionName => `app/posts/${actionName}`;
const DELETE_POST = createActionName('DELETE_POST')
const ADD_POST = createActionName('ADD_POST');











const CHANGE_STATUS = createActionNames('CHANGE_STATUS');


//selectors
export const getAllPosts = ({ posts }) => posts;

export const getPostById = ({ posts }, id) =>
  posts.find((post) => post.id === id);

// actions
const createActionName = (action) => {
  return `app/posts/${action}`;
};
const GET_DATA = createActionName('GET_DATA');
const EDIT_POST = createActionName('EDIT_POST');

// action creators
export const editPost = payload => ({ type: EDIT_POST, payload });
export const getData = (payload) => ({ type: GET_DATA, payload });
export const changeStatus = (payload) => ({ type: CHANGE_STATUS, payload });


export const fetchData = () => {
  return (dispatch) => {
    dispatch(changeStatus(true));
    fetch(`${API_URL}/posts`)
      .then((response) => response.json())
      .then((data) => dispatch(getData(data)))
      .then((data) => dispatch(editPost(data)))
  };
};

// reducer
export const postsReducer = (statePart = [], action) => {
  switch (action.type) {
    case GET_DATA:
      return [...action.payload];
    case CHANGE_STATUS: {
      return action.payload;
    }
    case EDIT_POST: {
      return statePart.map(post => (post.id === action.payload.id) ? { ...post, ...action.payload } : post);
    }
    default:
      return statePart;
  }
};
