import { useNavigate, useParams } from 'react-router-dom';
import { Button, Col, Form, Row, FormControl, Spinner } from 'react-bootstrap';
import { editTableRequest, getTableById } from '../../redux/tablesReducer';
import { useSelector } from 'react-redux';
import { useState } from 'react';
import { useDispatch } from 'react-redux';
import { useEffect } from 'react';
import { getStatus } from '../../redux/requestStatusReducer';

export const SingleTable = () => {
  const { id } = useParams();
  const loadedTable = useSelector((state) => getTableById(state, id));
  const isPending = useSelector((state) => getStatus(state));
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [table, setTable] = useState(
    loadedTable || { status: '', currentPeople: '', maxPeople: '', bill: '' }
  );

  const [status, setStatus] = useState(table.status);
  const [currentPeople, setCurrentPeople] = useState(table.currentPeople);
  const [maxPeople, setMaxPeople] = useState(table.maxPeople);
  const [bill, setBill] = useState(table.bill);

  useEffect(() => {
    if (loadedTable !== undefined) {
      setTable(loadedTable);
      setStatus(table.status);
      setCurrentPeople(table.currentPeople);
      setMaxPeople(table.maxPeople);
      setBill(table.bill);
    }
    if (loadedTable === undefined && isPending === false) {
      navigate('/');
    }
  }, [table, loadedTable, isPending, navigate]);

  const handleStatus = (value) => {
    if (value === 'Busy') {
      setBill('0');
    }
    if (value === 'Free' || value === 'Cleaning') {
      setCurrentPeople('0');
    }
    setStatus(value);
  };

  const handleCurrentPeople = (value) => {
    if (value < 0 || isNaN(value)) {
      value = '0';
    }
    if (value > Number(maxPeople)) {
      value = maxPeople;
    }
    setCurrentPeople(value);
  };

  const handleMaxPeople = (value) => {
    if (value < 0 || isNaN(value)) {
      value = '0';
    }
    if (value > 10) {
      value = '10';
    }
    setMaxPeople(value);
    if (Number(currentPeople) > value) {
      setCurrentPeople(value);
    }
  };

  const handleUpdate = (e) => {
    e.preventDefault();
    const editedTable = {
      id,
      status,
      currentPeople,
      maxPeople,
      bill,
    };
    dispatch(editTableRequest(editedTable));
    navigate('/');
  };

  if (loadedTable === undefined) {
    return <Spinner></Spinner>;
  }

  return (
    <Row className="justify-content-center">
      <Col className="col-12">
        <Form onSubmit={(e) => handleUpdate(e)}>
          <h1 className='my-3'>
            <strong>Table {table.id}</strong>
          </h1>

          <div className="mb-2" controlId="formBasicEmail">
            <div className="d-flex">
              <div className="p-1 flex me-2"><Form.Label style={({ marginRight: "5px" })}><strong>Status:</strong></Form.Label></div>
              <div className="">
                <Form.Select aria-label="Default select example"
                  value={status}
                  onChange={(e) => handleStatus(e.target.value)}
                  placeholder="Nr" style={{ width: "280px", height: "40px", }}
                >
                  <option value='Free'>Free</option>;
                  <option value='Reserved'>Reserved</option>;
                  <option value='Busy'>Busy</option>;
                  <option value='Cleaning'>Cleaning</option>;
                </Form.Select>
              </div>
            </div>
          </div>

          {status === 'Busy' ? (
            <div className="mb-2" controlId="formBasicEmail">
              <div className="d-flex">
                <div className="p-1 flex-2"><Form.Label style={({ marginLeft: "0px" })}><strong>Ammount:</strong></Form.Label></div>
                <div className="p-2 flex-2"><Form.Label style={({ marginLeft: "20px" })}>$</Form.Label></div>
                <div className="p-1 flex">
                  <FormControl
                    value={bill}
                    onChange={(e) => setBill(e.target.value)}
                    placeholder="Nr" style={{ width: "45px", height: "30px", marginRigt: "50px", }} />
                </div>
              </div>
            </div>
          ) : (
            ''
          )}

          <div className="mb-2" controlId="formBasicEmail">
            <div className="d-flex">
              <div className="p-1 flex-2 me-4"><Form.Label style={({ marginLeft: "0px" })}><strong>People:</strong></Form.Label></div>
              <div className="p-1 flex">
                <FormControl
                  input type="number"
                  min="1"
                  max="10"
                  value={currentPeople}
                  onChange={(e) => setCurrentPeople(e.target.value)}
                  placeholder="Nr" style={{ category: currentPeople.reserved ? "none" : "inline-block", width: "50px", height: "30px", textAlign: "center", marginRigt: "50px", }} />
              </div>
              <div className="p-2 flex-2">/</div>
              <div className="p-1 flex">
                <FormControl
                  value={maxPeople}
                  onChange={(e) => setMaxPeople(e.target.value)}
                  placeholder="Nr" style={{ width: "45px", height: "30px", marginRigt: "50px", }} />
              </div>
            </div>
          </div>



          <Button type='submit' className='mt-4'>
            Update
          </Button>

        </Form>
      </Col>
    </Row>
  );
};
