import { useNavigate, useParams } from 'react-router-dom';
import { Button, Col, Form, Row, FormControl, Spinner } from 'react-bootstrap';
import { editTableRequest } from '../../../redux/postsRedux';
import { useSelector } from 'react-redux';
import { useState } from 'react';
import { useDispatch } from 'react-redux';
import { useEffect } from 'react';
import { post2 } from '../../../post2';
import { useForm } from "react-hook-form";

const PostForm = (action) => {
  const { id } = useParams();
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [post] = useState(
    post2
  );

  const { register, handleSubmit: validate, formState: { errors } } = useForm();

  const handleSubmit = (e) => {
    e.preventDefault();
    const editedTable = {
      id,
      title,
      status,
      content1,
      content2,
      moneyAmmount,
    };
    dispatch(editTableRequest(editedTable));
    navigate('/');
  };

  const [title] = useState(post.title)
  const [status, setStatus] = useState(post.status);
  const [content1, setContent1] = useState(post.content1);
  const [content2, setContent2] = useState(post.content2);
  const [moneyAmmount, setMoneyAmmount] = useState(post.moneyAmmount);
  const [error, setError] = useState(""); // 2. Track the error in the input

  const handleStatus = (value) => {
    if (value === 'Busy') {
      setMoneyAmmount('0')
    }
    if (value === 'Free' || value === 'Cleaning') {
      setContent1('0');
    }
    setStatus(value);
  };

  const numberHandleChange = e => {
    e.preventDefault();
    const re = /^[0-9\b]+$/;
    if (e.target.value === "" || re.test(e.target.value)) {
      setMoneyAmmount(e.target.value);
      setError("");  // 3. Clear the error if input is valid
      console.log(e.target.value);
    } else {
      // alert("please check your phone number");
      // 4. Set the error on invalid input
      setError("❌ Only accept number characters between 0-9");
    }
  };



  return (
    <Row className="justify-content-center">
      <Col className="col-12">
        <form>
          <h2>
            <strong>{post.title}</strong>
          </h2>

          <div className="mb-2" controlId="formBasicEmail">
            <div className="d-flex">
              <div className="p-1 flex me-2"><Form.Label style={({ marginRight: "5px" })}><strong>Status:</strong></Form.Label></div>
              <div className="">
                <Form.Select aria-label="Default select example"
                  value={status}
                  onChange={(e) => handleStatus(e.target.value)}
                  placeholder="Nr" style={{ width: "280px", height: "40px", }}
                >
                  <option value='Free'>Free</option>;
                  <option value='Reserved'>Reserved</option>;
                  <option value='Busy'>Busy</option>;
                  <option value='Cleaning'>Cleaning</option>;
                </Form.Select>
              </div>
            </div>
          </div>

          {status === 'Busy' ? (
            <div className="mb-2" controlId="formBasicEmail">
              <div className="d-flex">
                <div className="p-1 flex-2"><Form.Label style={({ marginLeft: "0px" })}><strong>Ammount:</strong></Form.Label></div>
                <div className="p-2 flex-2"><Form.Label style={({ marginLeft: "20px" })}>$</Form.Label></div>
                <div className="p-1 flex">
                  <FormControl
                    {...register("moneyAmmount", { required: true })}
                    value={moneyAmmount}
                    onChange={(e) => numberHandleChange(e.target.value)}
                    placeholder="Nr" style={{ width: "45px", height: "30px", marginRigt: "50px", }} />
                  {errors.moneyAmmount && <span>This field is required</span>}
                </div>
              </div>
            </div>
          ) : (
            ''
          )}

          <div className="mb-2" controlId="formBasicEmail">
            <div className="d-flex">
              <div className="p-1 flex-2 me-4"><Form.Label style={({ marginLeft: "0px" })}><strong>People:</strong></Form.Label></div>
              <div className="p-1 flex">
                <FormControl
                  input type="number"
                  min="1"
                  max="10"
                  value={content1}
                  onChange={(e) => setContent1(e.target.value)}
                  placeholder="Nr" style={{ category: content1.reserved ? "none" : "inline-block", width: "58px", height: "30px", textAlign: "center", marginRigt: "50px", }} />
              </div>
              <div className="p-2 flex-2">/</div>
              <div className="p-1 flex">
                <FormControl
                  input type="number"
                  min="1"
                  max="10"
                  value={content2}
                  onChange={(e) => setContent2(e.target.value)}
                  placeholder="Nr" style={{ width: "58px", height: "30px", marginRigt: "50px", }} />
              </div>
            </div>
          </div>
          <Button onClick={(e) => validate(handleSubmit(e))}>Add post
          </Button>
        </form>
      </Col>
    </Row>
  );
};

export default PostForm
