import { useState } from "react";
import { Button, Col, Form, FormGroup, Row } from "react-bootstrap";


const PostForm = ({ action, ...props }) => {


  const [author2, setAuthor2] = useState(props.author || "");
  const [title, setTitle] = useState(props.title || "");
  const [author, setAuthor] = useState(props.author || "");
  const [description, setDescription] = useState(props.description || "");

  const handleSubmit = () => {

    action({ title, author, author2, description });

  };

  return (
    <Row className="justify-content-center">
      <Col className="col-7">
        <Form>
          <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Label>Title</Form.Label>
            <Form.Control
              value={title}
              onChange={e => setTitle(e.target.value)}
              type="text"
              placeholder="Enter title"
            />
          </Form.Group>

          <FormGroup>
            <Form.Label>Author</Form.Label>
            <Form.Control
              value={author}
              onChange={e => setAuthor(e.target.value)}
              type="text"
              placeholder="Enter author"
            />
          </FormGroup>

          <FormGroup>
            <Form.Label>Author2</Form.Label>
            <Form.Control
              value={author2}
              onChange={e => setAuthor2(e.target.value)}
              type="text"
              placeholder="Enter author2"
            />
          </FormGroup>

          <FormGroup>
            <Form.Label>Short description</Form.Label>
            <Form.Control
              value={description}
              onChange={e => setDescription(e.target.value)}
              type="text"
              placeholder="Leave a comment here"
            />
          </FormGroup>

          <Button
            onClick={handleSubmit}
            className="my-3">Add post
          </Button>
        </Form>
      </Col>
    </Row>
  )
}

export default PostForm;
