import { API_URL } from '../config';

const createActionNames = (action) => {
  return `app/status/${action}`;
};

const CHANGE_STATUS = createActionNames('CHANGE_STATUS');


//selectors
export const getAllTables = ({ tables }) => tables;

export const getTableById = ({ tables }, id) =>
  tables.find((table) => table.id === id);

// actions
const createActionName = (action) => {
  return `app/tables/${action}`;
};

const GET_DATA = createActionName('GET_DATA');

// action creators
export const getData = (payload) => ({ type: GET_DATA, payload });

export const changeStatus = (payload) => ({ type: CHANGE_STATUS, payload });

export const fetchData = () => {
  return (dispatch) => {
    dispatch(changeStatus(true));
    fetch(`${API_URL}/tables`)
      .then((response) => response.json())
      .then((data) => dispatch(getData(data)))
  };
};

// reducer
export const tablesReducer = (statePart = [], action) => {
  switch (action.type) {
    case GET_DATA:
      return [...action.payload];
    case CHANGE_STATUS: {
      return action.payload;
    }
    default:
      return statePart;
  }
};
