//selectors
export const getAllPosts = ({ posts }) => posts;
export const getAllPostById = ({ posts }, postId) => posts.find(post => post.id === postId);
// actions

// action creators
const postsReducer = (statePart = [], action) => {
  switch (action.type) {
    default:
      return statePart;
  };
};

export default postsReducer;
