import { useState } from "react";
import { Button, Card, Col, Modal, Row } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { Link, Navigate, useParams } from "react-router-dom";
import { getPostById } from "../../../redux/postsRedux";
import { deletePost } from "../../../redux/postsRedux";
import React from 'react';


const SinglePost = () => {

  const { id } = useParams();
  const postData = useSelector(state => getPostById(state, id));

  const [isShow, invokeModal] = React.useState(false)
  const initModal = () => {
    return invokeModal(!false)
    return invokeModal(true);
  }


  const [show, setShow] = useState(false);
  const buttonHide = () => setShow(false);
  const buttonShow = () => setShow(true);

  const dispatch = useDispatch();
  const deletePostSubmit = () => {
    dispatch(deletePost(postData.id))
    setShow();
  };

  if (!postData) return <Navigate to="/" />;
  else
    return (
      <>
        <Row className="center">
          <Col className="col-9">
            <Card className="border-0">
              <Card.Body>
                <Card.Title><h1>{postData.title}</h1></Card.Title>
                <Card.Text className="my-0"><strong>Author: </strong>{postData.author}</Card.Text>
                <Card.Text className="my-0"><strong>Published: </strong>{(postData.publishedDate)}</Card.Text>
              </Card.Body>
            </Card>
          </Col>
          <Col className="col-3">
            <>
              <Button variant="success" onClick={initModal}>
                Open Modal
              </Button>
              <Modal show={isShow}>
                <Modal.Header closeButton onClick={initModal}>
                  <Modal.Title>React Modal Popover Example</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                  Lorem ipsum dolor sit amet, consectetur adipiscing elit.
                </Modal.Body>
                <Modal.Footer>
                  <Button variant="danger" onClick={initModal}>
                    Close
                  </Button>
                  <Button variant="dark" onClick={initModal}>
                    Store
                  </Button>
                </Modal.Footer>
              </Modal>
            </>
          </Col>
        </Row>
      </>

    )
}

export default SinglePost;
