import { useState } from "react";
import { Button, button, Card, Col, Modal, Row } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { Link, Navigate, useParams } from "react-router-dom";
import { getPostById } from "../../../redux/postsRedux";
import { deletePost } from "../../../redux/postsRedux";


const SinglePost = () => {

  const { id } = useParams();
  const postData = useSelector(state => getPostById(state, id));

  const [show, setShow] = useState(false);
  const buttonHide = () => setShow(false);
  const buttonShow = () => setShow(true);

  const dispatch = useDispatch();
  const deletePostSubmit = () => {
    dispatch(deletePost(postData.id))
    setShow();
  };

  if (!postData) return <Navigate to="/" />;
  else
    return (
      <>
        <Row className="center">
          <Col className="col-9">
            <Card className="border-0">
              <Card.Body>
                <Card.Title><h1>{postData.title}</h1></Card.Title>
                <Card.Text className="my-0"><strong>Author: </strong>{postData.author}</Card.Text>
                <Card.Text className="my-0"><strong>Published: </strong>{(postData.publishedDate)}</Card.Text>
              </Card.Body>
            </Card>
          </Col>
          <Col className="col-3">
            <button type="button" class="btn btn-outline-info" data-toggle="modal" data-target="#exampleModal">Edit</button>
            <button type="button" class="btn btn-outline-danger">Delete</button>

            <div class="modal fade" id="exampleModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
              <div class="modal-dialog" role="document">
                <div class="modal-content">
                  <div class="modal-header">
                    <h5 class="modal-title" id="exampleModalLabel">Modal title</h5>
                    <button type="button" class="close" data-dismiss="modal" aria-label="Close">
                      <span aria-hidden="true">&times;</span>
                    </button>
                  </div>
                  <div class="modal-body">
                    ...
                  </div>
                  <div class="modal-footer">
                    <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
                    <button type="button" class="btn btn-primary">Save changes</button>
                  </div>
                </div>
              </div>
            </div>
          </Col>
        </Row>
      </>
    )
}

export default SinglePost;
