import shortid from "shortid";

export const getAllPosts = ({ posts }) => posts;
export const getPostByID = ({ posts }, postsId) => posts.find(post => post.id === postsId);
export const getPostsByCategory = ({ posts }, category) => posts.filter(post => post.category === category);

export const deletePost = payload => ({ type: DELETE_POST, payload })
export const addPost = payload => ({ type: ADD_POST, payload });
export const editPost = payload => ({ type: EDIT_POST, payload });

const createActionName = actionName => `app/posts/${actionName}`;

const GET_DATA = createActionName('GET_DATA');

const EDIT_TABLE = createActionName('EDIT_TABLE');

export const getData = (payload) => ({ type: GET_DATA, payload });
export const editTable = (payload) => ({ type: EDIT_TABLE, payload });

const DELETE_POST = createActionName('DELETE_POST')
const ADD_POST = createActionName('ADD_POST');
const EDIT_POST = createActionName('EDIT_POST');

const postsReducer = (statePart = [], action) => {
  switch (action.type) {
    case EDIT_TABLE:
      return statePart.map((table) => {
        if (table.id === action.payload.id) {
          return { ...table, ...action.payload };
        } else {
          return table;
        }
      });
    case GET_DATA:
      return [...action.payload];

    case DELETE_POST:
      return statePart.filter(post => post.id !== action.payload)
    case ADD_POST:
      return [...statePart, { ...action.payload, id: shortid() }];
    case EDIT_POST:
      return statePart.map(post => (post.id === action.payload.id) ? { ...post, ...action.payload } : post);
    default:
      return statePart;
  };
};

export default postsReducer;
