import { useState } from "react";
import { Button, Col, Form, Row } from "react-bootstrap";
import "react-datepicker/dist/react-datepicker.css";
import 'react-quill/dist/quill.snow.css'; // ES6
import { useForm } from 'react-hook-form';
import { FormControl } from 'react-bootstrap';
import { CATEGORIES } from '../../../consts';


const SingleTable = (action) => {
  const [post] = useState(
    { status: '', currentPeople: '', maxPeople: '', bill: '' }
  );


  const handleUpdate = (e) => {
    e.preventDefault();
    const editedTable = {
      id,
      status,
      currentPeople,
      maxPeople,
      bill,
    };
    dispatch(editTableRequest(editedTable));
    navigate('/');
  };


  const [status, setStatus] = useState(post.status);
  const [currentPeople, setCurrentPeople] = useState(post.currentPeople);
  const [maxPeople, setMaxPeople] = useState(post.maxPeople);
  const [bill, setBill] = useState(post.bill);




  const handleStatus = (value) => {
    if (value === 'Busy') {
      setBill('0');
    }
    if (value === 'Free' || value === 'Cleaning') {
      setCurrentPeople('0');
    }
    setStatus(value);
  };


  return (
    <Row className="justify-content-center">
      <Col className="col-12">
        <form onSubmit={(e) => handleUpdate(e)}>
          <h2>
            <strong>post {post.id}</strong>
          </h2>

          <div className="mb-2" controlId="formBasicEmail">
            <div className="d-flex">
              <div className="p-1 flex me-2"><Form.Label style={({ marginRight: "5px" })}><strong>Status:</strong></Form.Label></div>
              <div className="">
                <Form.Select aria-label="Default select example"
                  value={status}
                  onChange={(e) => handleStatus(e.target.value)}
                  placeholder="Nr" style={{ width: "280px", height: "40px", }}
                >
                  <option value='Free'>Free</option>;
                  <option value='Reserved'>Reserved</option>;
                  <option value='Busy'>Busy</option>;
                  <option value='Cleaning'>Cleaning</option>;
                </Form.Select>
              </div>
            </div>
          </div>

          {status === 'Busy' ? (
            <div className="mb-2" controlId="formBasicEmail">
              <div className="d-flex">
                <div className="p-1 flex-2"><Form.Label style={({ marginLeft: "0px" })}><strong>Ammount:</strong></Form.Label></div>
                <div className="p-2 flex-2"><Form.Label style={({ marginLeft: "20px" })}>$</Form.Label></div>
                <div className="p-1 flex">
                  <FormControl
                    value={bill}
                    onChange={(e) => setBill(e.target.value)}
                    placeholder="Nr" style={{ width: "45px", height: "30px", marginRigt: "50px", }} />
                </div>
              </div>
            </div>
          ) : (
            ''
          )}

          <div className="mb-2" controlId="formBasicEmail">
            <div className="d-flex">
              <div className="p-1 flex-2 me-4"><Form.Label style={({ marginLeft: "0px" })}><strong>People:</strong></Form.Label></div>
              <div className="p-1 flex">
                <FormControl
                  input type="number"
                  min="1"
                  max="10"
                  value={currentPeople}
                  onChange={(e) => setCurrentPeople(e.target.value)}
                  placeholder="Nr" style={{ category: currentPeople.reserved ? "none" : "inline-block", width: "58px", height: "30px", textAlign: "center", marginRigt: "50px", }} />
              </div>
              <div className="p-2 flex-2">/</div>
              <div className="p-1 flex">
                <FormControl
                  input type="number"
                  min="1"
                  max="10"
                  value={maxPeople}
                  onChange={(e) => setMaxPeople(e.target.value)}
                  placeholder="Nr" style={{ width: "58px", height: "30px", marginRigt: "50px", }} />
              </div>
            </div>
          </div>
          <Button type="submit">Add post
          </Button>

        </form>
      </Col>
    </Row>
  );
};

export default SingleTable
